namespace Lof\MarketPlace\Block\Adminhtml\Rating\Edit;

/**
 * Admin page left menu
 */
class Tabs extends \Magento\Backend\Block\Widget\Tabs
{
    /**
     * @return void
     */
    protected function _construct()
    {
        parent::_construct();
        $this->setId('page_tabs');
        $this->setDestElementId('edit_form');
        $this->setTitle(__('Rating Information'));
   

         $this->addTab(
            'main_section',
            [
             'label'   => __('General Infomration'),
             'content' => $this->getLayout()->createBlock('Lof\MarketPlace\Block\Adminhtml\Rating\Edit\Tab\Main')->toHtml(),
            ]
        );
    }
}
